import React, { useEffect, useRef, useState, } from 'react';
import { Check, Undo2, XCircle } from 'lucide-react';
import GameOverScreen from './GameOverScreen';
import { Home } from 'lucide-react';

export default function GameScreen({

  socket,
  turnData,
  board,
  setBoard,
  scores,
  statusMessage,
  showGameOver,
  gameOverData,
  gameEnded,
  onGameOver,
  rematchPending,
  username,
  profileImage,
  opponentImage,
  opponentUsername,
  setRematchPending,
  setCurrentScreen,
  onRematch,
  isReconnect
}) {
  console.log('[GameScreen] üí° isReconnect prop:', isReconnect);

const [selectedCell, setSelectedCell] = useState(null);
const [enteredLetter, setEnteredLetter] = useState('');
const [tempPosition, setTempPosition] = useState(null);
const [message, setMessage] = useState('');

useEffect(() => {
  console.log('[GameScreen] isReconnect:', isReconnect);
    if (isReconnect) {
      setMessage('‚è™ Kaldƒ±ƒüƒ±n yerden devam ediyorsun.');
      const timer = setTimeout(() => setMessage(''), 3000);
      return () => clearTimeout(timer);
    }
  }, [isReconnect]);

const [turnInfo, setTurnInfo] = useState('');
const [timer, setTimer] = useState('');
const [remainingTime, setRemainingTime] = useState(0);
const [confirmDisabled, setConfirmDisabled] = useState(false);
const [lastOpponentUsername, setLastOpponentUsername] = useState('');
const timerRef = useRef(null);
const [isMyTurn, setIsMyTurn] = useState(false);
const [letterToPlace, setLetterToPlace] = useState(null);
const buttonsDisabled = !isMyTurn || letterToPlace !== null;

const [gameStartedBefore, setGameStartedBefore] = useState(false);
const [selectedLetter, setSelectedLetter] = useState(null);
// useState'leri component √ºst√ºne ekle
const [previewLetter, setPreviewLetter] = useState(null);         // Basƒ±lan harf
const [previewPosition, setPreviewPosition] = useState({ x: 0, y: 0 }); // Parmaƒüƒ±n ekran pozisyonu
const letterRefs = useRef([]); // T√ºm harf kutularƒ±nƒ± takip etmek i√ßin ref dizisi
const isMenuButtonDisabled = false;
console.log('[DEBUG] profileImage:', profileImage);
console.log('[DEBUG] opponentImage:', opponentImage);

console.log('[DEBUG] GameScreen a√ßƒ±ldƒ±');
console.log('[DEBUG] gameOverData:', gameOverData);
console.log('[DEBUG] lastOpponentUsername:', lastOpponentUsername);
const [activeGames, setActiveGames] = useState([]);
const [storedOpponentImage, setStoredOpponentImage] = useState(() => {
    return localStorage.getItem('opponentAvatar') || '/default-avatar.png';
  });
console.log('storedOpponentImage:', storedOpponentImage);
useEffect(() => {
  console.log('activeGames deƒüi≈üti, yeni avatar:', activeGames?.[0]?.opponentImage);
    if (activeGames && activeGames.length > 0) {
      const currentOpponentAvatar = activeGames[0].opponentImage;
      if (currentOpponentAvatar && currentOpponentAvatar !== storedOpponentImage) {
        setStoredOpponentImage(currentOpponentAvatar);
        localStorage.setItem('opponentAvatar', currentOpponentAvatar);
      }
    }
  }, [activeGames]);

const fullProfileImage = profileImage
  ? (profileImage.startsWith('http') ? profileImage : window.location.origin + profileImage)
  : '/default-avatar.png';

  const fullOpponentImage = storedOpponentImage
    ? (storedOpponentImage.startsWith('http') ? storedOpponentImage : window.location.origin + storedOpponentImage)
    : '/default-avatar.png';

const startTimer = (duration) => {
  if (timerRef.current) clearInterval(timerRef.current);

  let timeLeft = duration;

  const formatTime = (ms) => {
    const totalSeconds = Math.floor(ms / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    if (totalSeconds <= 0) return '‚è∞ S√ºre doldu';
    
     // Saatlik g√∂sterim (1 saatten fazla ise)
  if (totalSeconds >= 3600) {
    // √ñrneƒüin 5 saat 15 dakika varsa ‚Üí "6 saat" g√∂stermeliyiz
    const roundedHour = Math.ceil(totalSeconds / 3600);
    return `${roundedHour} saat`;
  }

  // 1 dakika ile 59 dakika arasƒ±
  if (totalSeconds >= 60) {
    return `${minutes} dakika`;
  }

  // Son dakika: Dakika:saniye formatƒ±
  return `${minutes}:${seconds.toString().padStart(2, '0')}`;
};
  // ƒ∞lk g√∂sterim
  setTimer(formatTime(timeLeft));

  timerRef.current = setInterval(() => {
    timeLeft -= 1000;

    setTimer(formatTime(timeLeft));

    if (timeLeft <= 0) {
      clearInterval(timerRef.current);
      timerRef.current = null;
      setTimer('‚è∞ S√ºre doldu');
      setMessage('‚è∞ Hamle s√ºresi sona erdi.');
      setTurnInfo('');
    }
  }, 1000);
};
const stopTimer = () => {
  if (timerRef.current) {
    clearInterval(timerRef.current);
    timerRef.current = null;
  }

  setTimer(''); // ‚¨ÖÔ∏è DOM'a deƒüil, state'e yazƒ±yoruz
};


const handleCellClick = (row, col) => {
    if (gameEnded) {
  return;
}

// üõë Eƒüer sƒ±ra sende deƒüilse ve rakipten gelen harf de yoksa, tƒ±klama iptal
if (!isMyTurn && !letterToPlace) {
  setMessage('≈ûu an hamle sƒ±rasƒ± sende deƒüil!');
  return;
}

    const cellValue = board[row][col];

    if (cellValue !== '') {
      setMessage('Bu h√ºcre dolu ve kalƒ±cƒ±, deƒüi≈ütiremezsin!');
      return;
    }

    const clicked = { row, col };

    if (letterToPlace) {
    const newBoard = board.map(r => [...r]);
    newBoard[row][col] = letterToPlace;
    setBoard(newBoard);
    socket.send(JSON.stringify({ type: 'placeOpponentLetter', row, col }));
    setMessage(`Rakibin harfi "${letterToPlace}" konumuna yerle≈ütirildi.`);
    setLetterToPlace(null);
    setEnteredLetter('');
    setTempPosition(null);
    return;
  }
  if (selectedLetter) {
    const newBoard = board.map(r => [...r]);
    newBoard[row][col] = selectedLetter;
    setBoard(newBoard);
    setEnteredLetter(selectedLetter);
    setTempPosition(clicked);
    setSelectedCell(clicked);
    setMessage(`"${selectedLetter}" harfi se√ßilen kutuya yerle≈ütirildi.`);
    return;
  }

    if (tempPosition && enteredLetter) {
      if (tempPosition.row !== row || tempPosition.col !== col) {
        socket.send(JSON.stringify({
          type: 'updateOwnLetterPlacement',
          letter: enteredLetter,
          row,
          col,
          oldRow: tempPosition.row,
          oldCol: tempPosition.col
        }));
        setTempPosition(clicked);
        setMessage(`Harf (${enteredLetter}) yeni konuma ta≈üƒ±ndƒ±.`);
      }
    } else {
      setSelectedCell(clicked);
      setEnteredLetter(board[row][col] || '');
      setMessage('H√ºcre se√ßildi. Klavyeden harf girin.');
    }
};

useEffect(() => {
    if (opponentImage) {
      setStoredOpponentImage(opponentImage);
      localStorage.setItem('opponentAvatar', opponentImage);
    }
  }, [opponentImage]);


// üß† Klavye dinleyici
useEffect(() => {
    const handleKeyDown = (e) => {
      if (!selectedCell || letterToPlace || gameEnded) return;

      const rawKey = e.key;
      const letter = e.key.toLocaleUpperCase('tr-TR');
      if (forbiddenLetters.includes(letter)) {
      e.preventDefault();
      setMessage(`"${letter}" harfi kullanƒ±lamaz.`);
      return;
    }
      if (!/^[A-Z√áƒûƒ∞√ñ≈û√ú]$/.test(letter)) return;

      setEnteredLetter(letter);
      setTempPosition({ row: selectedCell.row, col: selectedCell.col });

      const newBoard = board.map(r => [...r]);
      newBoard[selectedCell.row][selectedCell.col] = letter;
      setBoard(newBoard);

      setMessage(`"${letter}" harfi ge√ßici olarak se√ßilen kutuya yerle≈ütirildi.`);

      if (e.key === 'Enter') {
  e.preventDefault();
  confirmOwnLetter(); // üëà Enter‚Äôa basƒ±ldƒ±ƒüƒ±nda harfi sunucuya g√∂nder
}

    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
}, [selectedCell, tempPosition, enteredLetter, gameEnded, board, socket]);

// ‚¨áÔ∏è State'lerin altƒ±na ekle (√∂rn. const [message, setMessage] = useState(''); varsa onun altƒ±)
const updateTurnMessages = (yourTurn, letterToPlace) => {
  console.log('[DEBUG] updateTurnMessages √ßaƒürƒ±ldƒ±:', { yourTurn, letterToPlace });
  if (yourTurn && letterToPlace) {
    setMessage('Harfi klavyeden girip bo≈ü bir h√ºcreye yerle≈ütir.');
    setTurnInfo('Rakipten gelen harfi yerle≈ütir.');
  } else if (yourTurn && !letterToPlace) {
    setMessage('');
    setTurnInfo('Kendi harfini girip yerle≈ütir.');
  } else if (!yourTurn && letterToPlace) {
    setMessage(`Rakibin g√∂nderdiƒüi harf: "${letterToPlace}"`);
    setTurnInfo('Bu harfi uygun kutuya yerle≈ütir.');
  } else {
    setMessage('');
    setTurnInfo('Rakibin hamlesi bekleniyor...');
  }
};
const handleCellDrop = (row, col, e) => {
  e.preventDefault();
  const letter = e.dataTransfer.getData('text/plain');

  if (!letter || !isMyTurn) {
    setMessage('Sƒ±ra sende deƒüil ya da ge√ßersiz harf.');
    return;
  }

  if (letterToPlace) {
    // Rakipten gelen harfi yerle≈ütiriyoruz
    const newBoard = board.map(r => [...r]);
    if (newBoard[row][col] !== '') {
      setMessage('Bu kutu dolu!');
      return;
    }

    newBoard[row][col] = letter;
    setBoard(newBoard);

    socket.send(JSON.stringify({ type: 'placeOpponentLetter', row, col }));
    setLetterToPlace(null);
    setMessage(`Rakip harfi "${letter}" konumuna yerle≈ütirildi.`);
    return;
  } else {
    // Kendi se√ßtiƒüimiz harf
    const newBoard = board.map(r => [...r]);
    if (newBoard[row][col] !== '') {
      setMessage('Bu kutu dolu!');
      return;
    }

    newBoard[row][col] = letter;
    setBoard(newBoard);
    setEnteredLetter(letter);
    setTempPosition({ row, col });
    setSelectedCell({ row, col });
    setMessage(`"${letter}" harfi se√ßilen kutuya yerle≈ütirildi.`);
  }
};

const handleLetterClick = (letter) => {
  if (letterToPlace === null) {
    setSelectedLetter(letter);
  }
};


