  ].map((row, rowIndex) => (
    
    <div key={`row-${rowIndex}`}
  className="flex justify-center gap-[1px] sm:gap-[1px]">
   {row.map((letter, i) => {
    const isInactive = ['Q', 'W', 'X'].includes(letter);
    return (
        <div
          key={`letter-${rowIndex}-${i}`}
          ref={(el) => {
  if (el) letterRefs.current.push(el);
}}  // ‚¨ÖÔ∏è Bu kutuyu refs listesine ekliyoruz
  data-letter={letter}                       // ‚¨ÖÔ∏è Bu kutunun hangi harf olduƒüunu belirtiyoruz


          onClick={() => {
              if (!isInactive) handleLetterClick(letter);
            }}
            onTouchStart={(e) => {
    if (!isInactive) {
      const touch = e.touches[0];
      setPreviewLetter(letter);                                // Harfi g√∂ster
      setPreviewPosition({ x: touch.clientX, y: touch.clientY }); // Parmaƒüƒ±n konumu
    }
  }}
  onTouchEnd={(e) => {
  const touch = e.changedTouches[0]; // üü° Parmaƒüƒ±n bƒ±rakƒ±ldƒ±ƒüƒ± pozisyonu al
  setPreviewLetter(null); // üü° √ñnizlemeyi kaldƒ±r

  const hoveredLetter = letterRefs.current.find(el => {
    const rect = el.getBoundingClientRect();
    return (
      touch.clientX >= rect.left &&
      touch.clientX <= rect.right &&
      touch.clientY >= rect.top &&
      touch.clientY <= rect.bottom
    );
  });

  if (hoveredLetter) {
    const selected = hoveredLetter.getAttribute('data-letter');
    if (!['Q', 'W', 'X'].includes(selected)) {
      handleLetterClick(selected); // üü¢ Bƒ±rakƒ±ldƒ±ƒüƒ±nda o harfi ger√ßek se√ßim yap
      setSelectedLetter(selected); // üü¢ UI'da da g√∂ster
    }
  }
}}
  onTouchMove={(e) => {
  const touch = e.touches[0];
  setPreviewPosition({ x: touch.clientX, y: touch.clientY }); // ‚¨ÖÔ∏è Parmaƒüƒ±n pozisyonunu g√ºncelle
